(function (tinymce) {

    tinymce.PluginManager.add('romano83', function(editor) {
        var $ = window.jQuery,
            rg = window.rg || {};

        if ($) {
            $(document).triggerHandler('tinymce-editor-setup', [editor]);
        }

        editor.on('init', function() {

            if ( $ ) {
                $( document ).triggerHandler( 'tinymce-editor-init', [editor] );
            }

        });

        editor.rg = editor.rg || {};

    });

    (function (window, rg, $){

        var views = {},
            instances = {};

        rg.mce = rg.mce || {};

        rg.mce.views = {

            register: function (type, extend) {
                views[type] = rg.mce.View.extend(_.extend(extend, {type: type}));
            },

            setMarkers: function(content) {
                var pieces = [{content: content}]
            }

        };

    })(window, window.rg, $);

})(tinymce);

